import React, { useState, useEffect } from 'react';
import { API_BASE } from '../config/config';

const CATEGORIE = {
  '✅ Lavori speciali / Edili': [
    'Rimozione neve da tetti',
    'Messa in sicurezza cornicioni o elementi pericolanti',
    'Riparazione coperture e tetti',
    'Rifacimento intonaci o pittura facciate',
    'Interventi su canne fumarie',
    'Montaggio pannelli solari termici',
  ],
  '✅ Manutenzione e Pulizia': [
    'Pulizia tetti',
    'Pulizia grondaie',
    'Pulizia vetri/vetrate in quota',
    'Pulizia pannelli fotovoltaici',
    'Pulizia facciate',
    'Pulizia silos e ciminiere',
  ],
  '✅ Ispezioni e Controlli': [
    'Ispezione facciate',
    'Ispezione coperture industriali',
    'Ispezione pale eoliche',
    'Ispezione torri e antenne',
    'Ispezione con droni',
  ],
  '✅ Installazioni': [
    'Installazione antenne TV o parabole',
    'Installazione impianti fotovoltaici',
    'Installazione climatizzatori in quota',
    'Installazione insegne e cartellonistica',
    'Installazione linea vita',
    'Installazione parafulmini',
    'Montaggio ponteggi mobili temporanei',
  ],
  '✅ Taglio e Verde': [
    'Potatura alberi ad alto fusto',
    'Abbattimento controllato alberi',
    'Potatura siepi in quota',
  ],
  '✅ Altro': ['Altro (specifica…)']
};

const RichiestaForm = () => {
  const [categoria, setCategoria] = useState('');
  const [localita, setLocalita] = useState('');
  const [descrizione, setDescrizione] = useState('');
  const [file, setFile] = useState(null);
  const [comuni, setComuni] = useState([]);
  const [suggestions, setSuggestions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [messaggio, setMessaggio] = useState('');

  useEffect(() => {
    fetch('https://comuni-ita-api.vercel.app/api/comuni')
      .then((res) => res.json())
      .then((data) => setComuni(data))
      .catch((err) => console.error('Errore caricamento comuni:', err));
  }, []);

  const handleLocalitaChange = (e) => {
    const val = e.target.value;
    setLocalita(val);
    setSuggestions(
      comuni.filter((c) =>
        c.nome.toLowerCase().startsWith(val.toLowerCase())
      ).slice(0, 10)
    );
  };

  const handleSelectComune = (nome) => {
    setLocalita(nome);
    setSuggestions([]);
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    const token = localStorage.getItem('token');
    let media_url = '';

    try {
      if (file) {
        const formData = new FormData();
        formData.append('immagine', file);
        const uploadRes = await fetch(`${API_BASE}/api/upload`, {
          method: 'POST',
          headers: { Authorization: `Bearer ${token}` },
          body: formData
        });
        const uploadData = await uploadRes.json();
        media_url = uploadData.url;
      }

      const body = { categoria, localita, descrizione, media_url };
      const res = await fetch(`${API_BASE}/api/richiesta`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify(body)
      });

      if (res.ok) {
        setMessaggio('✅ Richiesta inviata con successo!');
        setCategoria('');
        setLocalita('');
        setDescrizione('');
        setFile(null);
      } else {
        const err = await res.json();
        setMessaggio(`❌ Errore: ${err.error || 'invio fallito'}`);
      }
    } catch (err) {
      console.error(err);
      setMessaggio('❌ Errore di rete');
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: '600px', margin: '2rem auto', padding: '1rem' }}>
      <h2>📝 Inserisci una nuova richiesta</h2>

      <label>Categoria</label>
      <select value={categoria} onChange={(e) => setCategoria(e.target.value)} required style={{ width: '100%', marginBottom: '1rem' }}>
        <option value="">Seleziona una categoria</option>
        {Object.entries(CATEGORIE).map(([group, options]) => (
          <optgroup key={group} label={group}>
            {options.map(opt => <option key={opt} value={opt}>{opt}</option>)}
          </optgroup>
        ))}
      </select>

      <label>Comune</label>
      <input
        type="text"
        value={localita}
        onChange={handleLocalitaChange}
        placeholder="Inserisci un comune"
        required
        style={{ width: '100%', marginBottom: '0.5rem' }}
      />
      {suggestions.length > 0 && (
        <ul style={{ listStyle: 'none', padding: 0, maxHeight: '100px', overflowY: 'auto' }}>
          {suggestions.map((c) => (
            <li key={c.nome} onClick={() => handleSelectComune(c.nome)} style={{ cursor: 'pointer' }}>
              {c.nome} ({c.provincia})
            </li>
          ))}
        </ul>
      )}

      <label>Descrizione dettagliata</label>
      <textarea
        value={descrizione}
        onChange={(e) => setDescrizione(e.target.value)}
        rows={5}
        required
        style={{ width: '100%', marginBottom: '1rem' }}
      />

      <label>Carica immagine (opzionale)</label>
      <input type="file" onChange={handleFileChange} accept="image/*" style={{ marginBottom: '1rem' }} />

      <button type="submit" disabled={loading}>
        {loading ? 'Invio...' : 'Invia richiesta'}
      </button>

      {messaggio && (
        <p style={{ marginTop: '1rem', color: messaggio.startsWith('✅') ? 'green' : 'red' }}>
          {messaggio}
        </p>
      )}
    </form>
  );
};

export default RichiestaForm;
