import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { toast } from 'react-toastify';
import { API_BASE } from '../config/config';
import SbloccaContattiButton from '../components/SbloccaContattiButton';

const DettaglioRichiesta = () => {
  const { id } = useParams();
  const [richiesta, setRichiesta] = useState(null);
  const [errore, setErrore] = useState('');
  const [contattiVisibili, setContattiVisibili] = useState(false);

  const fetchDettaglio = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`${API_BASE}/api/richiesta/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      if (res.ok) {
        setRichiesta(data);
        setContattiVisibili(data.sbloccata);
      } else {
        setErrore(data.error || 'Errore caricamento richiesta');
      }
    } catch (err) {
      setErrore('Errore di rete');
    }
  };

  const inviaEmailConferma = async () => {
    try {
      const token = localStorage.getItem('token');
      await fetch(`${API_BASE}/api/email-conferma-sblocco/${id}`, {
        method: 'POST',
        headers: { Authorization: `Bearer ${token}` },
      });
    } catch (err) {
      console.error('Errore invio email:', err);
    }
  };

  useEffect(() => {
    fetchDettaglio();
  }, [id]);

  const handleSbloccoSuccess = () => {
    setContattiVisibili(true);
    toast.success('‚úÖ Contatti sbloccati con successo!');
    inviaEmailConferma();
    fetchDettaglio();
  };

  if (!richiesta) return <p>‚è≥ Caricamento...</p>;

  return (
    <div style={{ padding: '1rem' }}>
      <h2>üìù Dettaglio Richiesta</h2>
      <p><strong>Categoria:</strong> {richiesta.categoria}</p>
      <p><strong>Regione:</strong> {richiesta.regione}</p>
      <p><strong>Provincia:</strong> {richiesta.provincia}</p>
      <p><strong>Comune:</strong> {richiesta.localita}</p>
      <p><strong>Descrizione:</strong> {richiesta.descrizione}</p>

      {contattiVisibili ? (
        <>
          <p><strong>Contatti:</strong> {richiesta.contatti || 'Non forniti'}</p>
        </>
      ) : (
        <SbloccaContattiButton richiestaId={richiesta.id} onSuccess={handleSbloccoSuccess} />
      )}

      {errore && <p style={{ color: 'red' }}>{errore}</p>}
    </div>
  );
};

export default DettaglioRichiesta;
