import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = () => {
  const navigate = useNavigate();
  const [utente, setUtente] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return;

    try {
      const payload = JSON.parse(atob(token.split('.')[1]));
      setUtente({ nome: payload.nome, ruolo: payload.ruolo });
    } catch (err) {
      console.error('Errore decoding token:', err);
      setUtente(null);
    }
  }, []);

  const logout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <nav style={{ padding: '1rem', background: '#eee', display: 'flex', justifyContent: 'space-between' }}>
      <div>
        <Link to="/">üè† Home</Link>{' | '}
        <Link to="/richieste-pubbliche">üîç Richieste Pubbliche</Link>{' | '}
        {utente && (
          <>
            <Link to="/home">Dashboard</Link>{' | '}
            <Link to="/mie-richieste">Le mie richieste</Link>{' | '}
            <Link to="/nuova-richiesta">Nuova richiesta</Link>{' | '}
            <Link to="/sblocchi-effettuati">Sblocchi effettuati</Link>{' | '}
            <button onClick={logout} style={{ background: 'none', border: 'none', color: 'blue', cursor: 'pointer' }}>
              üîì Logout
            </button>
          </>
        )}
        {!utente && (
          <>
            <Link to="/login">Login</Link>{' | '}
            <Link to="/register">Registrati</Link>
          </>
        )}
      </div>

      {utente && (
        <div style={{ fontStyle: 'italic' }}>
          üëã Benvenuto <strong>{utente.nome}</strong> ({utente.ruolo})
        </div>
      )}
    </nav>
  );
};

export default Navbar;
