import React, { useState } from 'react';
import { API_BASE } from '../config/config';

const CATEGORIE = [
  '‚úÖ Lavori speciali / Edili - Rimozione neve da tetti',
  '‚úÖ Lavori speciali / Edili - Messa in sicurezza cornicioni o elementi pericolanti',
  '‚úÖ Lavori speciali / Edili - Riparazione coperture e tetti',
  '‚úÖ Lavori speciali / Edili - Rifacimento intonaci o pittura facciate',
  '‚úÖ Lavori speciali / Edili - Interventi su canne fumarie',
  '‚úÖ Lavori speciali / Edili - Montaggio pannelli solari termici',
  '‚úÖ Manutenzione e Pulizia - Pulizia tetti',
  '‚úÖ Manutenzione e Pulizia - Pulizia grondaie',
  '‚úÖ Manutenzione e Pulizia - Pulizia vetri/vetrate in quota',
  '‚úÖ Manutenzione e Pulizia - Pulizia pannelli fotovoltaici',
  '‚úÖ Manutenzione e Pulizia - Pulizia facciate',
  '‚úÖ Manutenzione e Pulizia - Pulizia silos e ciminiere',
  '‚úÖ Ispezioni e Controlli - Ispezione facciate',
  '‚úÖ Ispezioni e Controlli - Ispezione coperture industriali',
  '‚úÖ Ispezioni e Controlli - Ispezione pale eoliche',
  '‚úÖ Ispezioni e Controlli - Ispezione torri e antenne',
  '‚úÖ Ispezioni e Controlli - Ispezione con droni',
  '‚úÖ Installazioni - Installazione antenne TV o parabole',
  '‚úÖ Installazioni - Installazione impianti fotovoltaici',
  '‚úÖ Installazioni - Installazione climatizzatori in quota',
  '‚úÖ Installazioni - Installazione insegne e cartellonistica',
  '‚úÖ Installazioni - Installazione linea vita',
  '‚úÖ Installazioni - Installazione parafulmini',
  '‚úÖ Installazioni - Montaggio ponteggi mobili temporanei',
  '‚úÖ Taglio e Verde - Potatura alberi ad alto fusto',
  '‚úÖ Taglio e Verde - Abbattimento controllato alberi',
  '‚úÖ Taglio e Verde - Potatura siepi in quota',
  '‚úÖ Altro - Altro (specifica...)'
];

const RichiestaForm = () => {
  const [categoria, setCategoria] = useState('');
  const [localita, setLocalita] = useState('');
  const [provincia, setProvincia] = useState('');
  const [regione, setRegione] = useState('');
  const [descrizione, setDescrizione] = useState('');
  const [contatti, setContatti] = useState('');
  const [urgenza, setUrgenza] = useState(false);
  const [file, setFile] = useState(null);
  const [messaggio, setMessaggio] = useState('');
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    const token = localStorage.getItem('token');
    let media_url = '';

    try {
      if (file) {
        const formData = new FormData();
        formData.append('immagine', file);

        const uploadRes = await fetch(`${API_BASE}/api/upload`, {
          method: 'POST',
          headers: { Authorization: `Bearer ${token}` },
          body: formData
        });

        const uploadData = await uploadRes.json();
        if (!uploadRes.ok) throw new Error(uploadData.error);
        media_url = uploadData.url;
      }

      const body = {
        categoria,
        localita,
        provincia,
        regione,
        descrizione,
        contatti,
        urgenza,
        media_url
      };

      const res = await fetch(`${API_BASE}/api/richiesta`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify(body)
      });

      const data = await res.json();
      if (res.ok) {
        setMessaggio('‚úÖ Richiesta inviata con successo!');
        setCategoria('');
        setLocalita('');
        setProvincia('');
        setRegione('');
        setDescrizione('');
        setContatti('');
        setUrgenza(false);
        setFile(null);
      } else {
        setMessaggio(`‚ùå Errore: ${data.error}`);
      }
    } catch (err) {
      console.error(err);
      setMessaggio('‚ùå Errore invio richiesta');
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: '600px', margin: '2rem auto' }}>
      <h2>üìù Inserisci una nuova richiesta</h2>

      <label>Categoria:</label>
      <select value={categoria} onChange={(e) => setCategoria(e.target.value)} required>
        <option value="">-- Seleziona --</option>
        {CATEGORIE.map((cat, idx) => (
          <option key={idx} value={cat}>{cat}</option>
        ))}
      </select><br /><br />

      <input type="text" placeholder="Comune" value={localita} onChange={(e) => setLocalita(e.target.value)} required />
      <input type="text" placeholder="Provincia" value={provincia} onChange={(e) => setProvincia(e.target.value)} required />
      <input type="text" placeholder="Regione" value={regione} onChange={(e) => setRegione(e.target.value)} required /><br /><br />

      <textarea placeholder="Descrizione dettagliata" value={descrizione} onChange={(e) => setDescrizione(e.target.value)} rows={5} required /><br />

      <input type="text" placeholder="Contatti aggiuntivi (opzionale)" value={contatti} onChange={(e) => setContatti(e.target.value)} /><br />

      <label>
        <input type="checkbox" checked={urgenza} onChange={() => setUrgenza(!urgenza)} />
        Urgente
      </label><br /><br />

      <input type="file" accept="image/*" onChange={handleFileChange} /><br /><br />

      <button type="submit" disabled={loading}>
        {loading ? 'Invio...' : 'Invia richiesta'}
      </button>

      {messaggio && <p style={{ color: messaggio.startsWith('‚úÖ') ? 'green' : 'red' }}>{messaggio}</p>}
    </form>
  );
};

export default RichiestaForm;
