import React, { useEffect, useState } from 'react';
import { API_BASE } from '../config/config';

const RichiesteLavoroInQuota = () => {
  const [richieste, setRichieste] = useState([]);
  const [provinciaFiltro, setProvinciaFiltro] = useState('');
  const [loading, setLoading] = useState(false);
  const [sblocchiEffettuati, setSblocchiEffettuati] = useState([]);

  useEffect(() => {
    // Carica richieste pubbliche
    const fetchRichieste = async () => {
      setLoading(true);
      try {
        const res = await fetch(`${API_BASE}/api/richieste/pubbliche`);
        const data = await res.json();
        setRichieste(data);
      } catch (err) {
        console.error('Errore nel caricamento delle richieste:', err);
      } finally {
        setLoading(false);
      }
    };

    // Carica richieste già sbloccate
    const fetchSblocchi = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) return;
        const res = await fetch(`${API_BASE}/api/sblocchi-effettuati`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        const data = await res.json();
        const idsSbloccati = data.map(r => r.id);
        setSblocchiEffettuati(idsSbloccati);
      } catch (err) {
        console.error('Errore nel caricamento sblocchi:', err);
      }
    };

    fetchRichieste();
    fetchSblocchi();
  }, []);

  // 🔓 Avvia Stripe solo se non già sbloccata
  const handleSbloccaConPagamento = async (richiestaId) => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert("Devi essere loggato per sbloccare una richiesta.");
      return;
    }

    if (sblocchiEffettuati.includes(richiestaId)) {
      alert('Hai già sbloccato questa richiesta.');
      return;
    }

    try {
      const res = await fetch(`${API_BASE}/api/checkout/sblocca`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({ richiesta_id: richiestaId })
      });

      const data = await res.json();

      if (data.url) {
        window.location.href = data.url;
      } else {
        alert('Errore nella creazione del pagamento');
      }
    } catch (err) {
      console.error('Errore nel pagamento:', err);
      alert('Errore durante il processo di pagamento.');
    }
  };

  // 🔍 Filtro per provincia
  const richiesteFiltrate = richieste.filter(r =>
    provinciaFiltro === '' || r.provincia.toLowerCase().includes(provinciaFiltro.toLowerCase())
  );

  return (
    <div style={{ padding: '1rem' }}>
      <h2>📢 Richieste di lavoro in quota</h2>
      <p>Visualizza richieste di lavoro in quota pubblicate da committenti in tutta Italia. Per accedere ai contatti è necessario sbloccare la richiesta.</p>

      <input
        type="text"
        placeholder="Filtra per provincia (es. Milano)"
        value={provinciaFiltro}
        onChange={(e) => setProvinciaFiltro(e.target.value)}
        style={{ margin: '1rem 0', padding: '0.5rem', width: '100%' }}
      />

      {loading ? (
        <p>⏳ Caricamento richieste...</p>
      ) : richiesteFiltrate.length === 0 ? (
        <p>Nessuna richiesta trovata.</p>
      ) : (
        <ul>
          {richiesteFiltrate.map((r) => (
            <li key={r.id} style={{ marginBottom: '1rem', border: '1px solid #ccc', padding: '1rem', borderRadius: '8px' }}>
              <p><strong>Categoria:</strong> {r.categoria}</p>
              <p><strong>Regione:</strong> {r.regione}</p>
              <p><strong>Provincia:</strong> {r.provincia}</p>
              <p><strong>Comune:</strong> {r.localita}</p>
              <p><strong>Descrizione:</strong> {r.descrizione.slice(0, 100)}...</p>

              <button
                onClick={() => handleSbloccaConPagamento(r.id)}
                style={{
                  marginTop: '0.5rem',
                  backgroundColor: '#007bff',
                  color: 'white',
                  border: 'none',
                  padding: '0.5rem 1rem',
                  cursor: 'pointer',
                  borderRadius: '5px'
                }}
              >
                🔓 Sblocca contatti
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default RichiesteLavoroInQuota;
