import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './Navbar';
import RichiestaForm from './RichiestaForm';
import RichiestePubbliche from './pages/RichiestePubbliche';
import DettaglioRichiesta from './pages/DettaglioRichiesta';
import Login from './pages/Login';
import Register from './pages/Register';
import RecuperaPassword from './pages/RecuperaPassword';
import Committente from './pages/Committente';
import Impresa from './pages/Impresa';
import Professionista from './pages/Professionista';
import PrivateRoute from './PrivateRoute';

// ✅ Import per il test
import TestLogin from './TestLogin';

function App() {
  const [utente, setUtente] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      fetch('http://localhost:5000/api/utente', {
        headers: { Authorization: `Bearer ${token}` },
      })
        .then(res => res.json())
        .then(data => setUtente(data))
        .catch(() => setUtente(null));
    }
  }, []);

  return (
    <Router>
      <Navbar utente={utente} setUtente={setUtente} />
      <div className="container" style={{ padding: '2rem' }}>
        <Routes>
          <Route path="/" element={<RichiestePubbliche />} />
          <Route path="/richiesta/:id" element={<DettaglioRichiesta />} />
          <Route path="/login" element={<Login setUtente={setUtente} />} />
          <Route path="/register" element={<Register />} />
          <Route path="/recupera-password" element={<RecuperaPassword />} />
          <Route
            path="/richiesta"
            element={
              <PrivateRoute utente={utente}>
                <RichiestaForm utente={utente} />
              </PrivateRoute>
            }
          />
          <Route path="/committente" element={<Committente utente={utente} />} />
          <Route path="/impresa" element={<Impresa utente={utente} />} />
          <Route path="/professionista" element={<Professionista utente={utente} />} />

          {/* ✅ Rotta test temporanea */}
          <Route path="/test-login" element={<TestLogin />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
