import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const RichiestePubbliche = () => {
  const [richieste, setRichieste] = useState([]);
  const [filtroCategoria, setFiltroCategoria] = useState('');
  const [filtroLocalita, setFiltroLocalita] = useState('');
  const [categorie, setCategorie] = useState([]);

  useEffect(() => {
    fetch('https://inquotus-backend-auth.onrender.com/api/richieste')
      .then((res) => res.json())
      .then((data) => {
        setRichieste(data);

        // Preleva categorie uniche
        const catUniche = [...new Set(data.map(r => r.categoria))];
        setCategorie(catUniche);
      })
      .catch((err) => console.error('Errore caricamento richieste:', err));
  }, []);

  const richiesteFiltrate = richieste.filter(r => {
    return (
      (filtroCategoria === '' || r.categoria === filtroCategoria) &&
      (filtroLocalita === '' || r.localita.toLowerCase().includes(filtroLocalita.toLowerCase()))
    );
  });

  return (
    <div style={{ maxWidth: '800px', margin: '2rem auto', padding: '1rem' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '2rem' }}>üìã Richieste disponibili</h2>

      {/* FILTRI */}
      <div style={{ marginBottom: '2rem' }}>
        <label><strong>Filtra per categoria:</strong>{' '}</label>
        <select value={filtroCategoria} onChange={(e) => setFiltroCategoria(e.target.value)}>
          <option value="">Tutte</option>
          {categorie.map((cat, index) => (
            <option key={index} value={cat}>{cat}</option>
          ))}
        </select>

        <label style={{ marginLeft: '1rem' }}><strong>Filtra per comune:</strong>{' '}</label>
        <input
          type="text"
          value={filtroLocalita}
          onChange={(e) => setFiltroLocalita(e.target.value)}
          placeholder="Es. Milano"
        />
      </div>

      {/* RISULTATI */}
      {richiesteFiltrate.length === 0 ? (
        <p style={{ textAlign: 'center' }}>‚è≥ Nessuna richiesta trovata.</p>
      ) : (
        richiesteFiltrate.map((r) => (
          <div key={r.id} style={{
            border: '1px solid #ddd',
            borderRadius: '10px',
            padding: '1.5rem',
            marginBottom: '1.5rem',
            boxShadow: '0 2px 6px rgba(0,0,0,0.05)'
          }}>
            <h3 style={{ margin: '0 0 1rem 0', color: '#333' }}>{r.categoria}</h3>
            <p><strong>üìç Comune:</strong> {r.localita}</p>
            <p><strong>üìÖ Data:</strong> {new Date(r.data).toLocaleDateString()}</p>
            <div style={{ marginTop: '1rem' }}>
              <Link to={`/richiesta/${r.id}`} style={{
                backgroundColor: '#007bff',
                color: 'white',
                padding: '0.5rem 1rem',
                borderRadius: '5px',
                textDecoration: 'none'
              }}>
                üîç Vedi dettagli
              </Link>
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default RichiestePubbliche;
