import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Register = () => {
  const [formData, setFormData] = useState({ nome: '', email: '', password: '', ruolo: 'committente' });
  const [errore, setErrore] = useState('');
  const navigate = useNavigate();

  const handleChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    setErrore('');
    try {
      const res = await fetch('https://inquotus-backend-auth.onrender.com/api/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });

      const data = await res.json();
      if (res.ok) {
        alert('Registrazione avvenuta con successo!');
        navigate('/login');
      } else if (data.error && data.error.includes('duplicate key')) {
        setErrore('Questa email è già registrata. Hai dimenticato la password?');
      } else {
        setErrore(data.error || 'Errore nella registrazione');
      }
    } catch (err) {
      setErrore('Errore di connessione al server');
    }
  };

  return (
    <div>
      <h2>Registrati</h2>
      {errore && <p style={{ color: 'red' }}>{errore}</p>}
      <form onSubmit={handleSubmit}>
        <input name="nome" placeholder="Nome e Cognome" onChange={handleChange} required /><br />
        <input name="email" type="email" placeholder="Email" onChange={handleChange} required /><br />
        <input name="password" type="password" placeholder="Password" onChange={handleChange} required /><br />
        <select name="ruolo" onChange={handleChange} value={formData.ruolo}>
          <option value="committente">Committente</option>
          <option value="esecutore">Impresa</option>
          <option value="tecnico">Professionista</option>
        </select><br /><br />
        <button type="submit">Registrati</button>
      </form>
      <p><a href="/recupera-password">Hai dimenticato la password?</a></p>
    </div>
  );
};

export default Register;
