// src/pages/RichiesteMie.jsx
import React, { useEffect, useState } from 'react';
import { API_BASE } from '../config';

const RichiesteMie = () => {
  const [richieste, setRichieste] = useState([]);
  const [errore, setErrore] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (!token) {
      setErrore('Utente non autenticato.');
      return;
    }

    fetch(`${API_BASE}/api/richieste/mie`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((res) => {
        if (!res.ok) throw new Error('Errore nel recupero delle richieste');
        return res.json();
      })
      .then((data) => setRichieste(data))
      .catch((err) => setErrore(err.message));
  }, []);

  return (
    <div style={{ padding: '1rem' }}>
      <h2>Le tue richieste</h2>
      {errore && <p style={{ color: 'red' }}>{errore}</p>}
      {richieste.length === 0 && !errore && <p>Nessuna richiesta trovata.</p>}
      <ul>
        {richieste.map((r) => (
          <li key={r.id} style={{ marginBottom: '1rem', border: '1px solid #ccc', padding: '1rem' }}>
            <strong>{r.categoria}</strong><br />
            <span>{r.localita}</span><br />
            <small>{new Date(r.data_inserimento).toLocaleString()}</small><br />
            <p>{r.descrizione}</p>
            {r.media_url && <img src={r.media_url} alt="Allegato" width="150" />}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RichiesteMie;
