import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const RichiestePubbliche = () => {
  const [richieste, setRichieste] = useState([]);

  useEffect(() => {
    fetch('https://inquotus-backend-auth.onrender.com/api/richieste')
      .then((res) => res.json())
      .then((data) => setRichieste(data))
      .catch((err) => console.error('Errore caricamento richieste:', err));
  }, []);

  return (
    <div style={{ maxWidth: '800px', margin: '2rem auto', padding: '1rem' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '2rem' }}>üìã Richieste disponibili</h2>

      {richieste.length === 0 ? (
        <p style={{ textAlign: 'center' }}>‚è≥ Nessuna richiesta trovata.</p>
      ) : (
        richieste.map((r) => (
          <div key={r.id} style={{
            border: '1px solid #ddd',
            borderRadius: '10px',
            padding: '1.5rem',
            marginBottom: '1.5rem',
            boxShadow: '0 2px 6px rgba(0,0,0,0.05)'
          }}>
            <h3 style={{ margin: '0 0 1rem 0', color: '#333' }}>{r.categoria}</h3>
            <p><strong>üìç Comune:</strong> {r.localita}</p>
            <p><strong>üìÖ Data:</strong> {new Date(r.data).toLocaleDateString()}</p>
            <div style={{ marginTop: '1rem' }}>
              <Link to={`/richiesta/${r.id}`} style={{
                backgroundColor: '#007bff',
                color: 'white',
                padding: '0.5rem 1rem',
                borderRadius: '5px',
                textDecoration: 'none'
              }}>
                üîç Vedi dettagli
              </Link>
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default RichiestePubbliche;
