import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Register = () => {
  const [formData, setFormData] = useState({
    nome: '',
    email: '',
    password: '',
    ruolo: 'committente'
  });
  const [showPassword, setShowPassword] = useState(false);
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const validatePassword = (password) => {
    const regex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[\W_]).{6,}$/;
    return regex.test(password);
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (!validatePassword(formData.password)) {
      return setMessage("La password deve contenere almeno 6 caratteri, una maiuscola, una minuscola, un numero e un simbolo.");
    }

    try {
      const res = await fetch('https://inquotus-backend-auth.onrender.com/api/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      const data = await res.json();
      if (res.ok) {
        setMessage('Registrazione completata!');
        navigate('/login');
      } else {
        setMessage(data.error || 'Errore nella registrazione');
      }
    } catch (error) {
      setMessage('Errore nel collegamento al backend');
    }
  };

  return (
    <div style={{ maxWidth: '400px', margin: '2rem auto' }}>
      <h2>Registrazione</h2>
      <form onSubmit={handleSubmit}>
        <input name="nome" placeholder="Nome" onChange={handleChange} required /><br /><br />
        <input name="email" placeholder="Email" type="email" onChange={handleChange} required /><br /><br />

        <div style={{ position: 'relative' }}>
          <input
            name="password"
            placeholder="Password"
            type={showPassword ? "text" : "password"}
            onChange={handleChange}
            required
            style={{ paddingRight: '2.5rem' }}
          />
          <span
            onClick={() => setShowPassword(!showPassword)}
            style={{
              position: 'absolute',
              right: '10px',
              top: '50%',
              transform: 'translateY(-50%)',
              cursor: 'pointer'
            }}
          >
            {showPassword ? '🙈' : '👁️'}
          </span>
        </div>
        <small>
          Almeno 6 caratteri, una maiuscola, una minuscola, un numero e un simbolo.
        </small><br /><br />

        <select name="ruolo" onChange={handleChange} value={formData.ruolo}>
          <option value="committente">Committente</option>
          <option value="esecutore">Impresa</option>
          <option value="tecnico">Professionista</option>
        </select><br /><br />

        <button type="submit">Registrati</button>
      </form>
      <p>{message}</p>
    </div>
  );
};

export default Register;
