import React, { useState } from 'react';
import CategoriaForm from './CategoriaForm';

const RichiestaForm = ({ userId }) => {
  const [formData, setFormData] = useState({
    categoria: '',
    localita: '',
    descrizione: '',
    media_url: ''
  });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('https://inquotus-backend-auth.onrender.com/api/richiesta', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ...formData, user_id: userId })
      });
      const data = await res.json();
      setMessage(res.ok ? 'Richiesta inviata!' : data.error || 'Errore invio richiesta');
    } catch (error) {
      setMessage('Errore di connessione al server');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <CategoriaForm onChange={handleChange} />
      <input
        name="localita"
        placeholder="LocalitÃ "
        onChange={handleChange}
        required
      /><br /><br />
      <textarea
        name="descrizione"
        placeholder="Descrizione dettagliata"
        onChange={handleChange}
      /><br /><br />
      <input
        name="media_url"
        placeholder="Link immagine (opzionale)"
        onChange={handleChange}
      /><br /><br />
      <button type="submit">Invia richiesta</button>
      <p>{message}</p>
    </form>
  );
};

export default RichiestaForm;
