import React from 'react';
import { Navigate, useLocation } from 'react-router-dom';

const PrivateRoute = ({ children, ruolo }) => {
  const token = localStorage.getItem('token');
  const location = useLocation();

  if (!token) {
    // Salva la pagina di origine per il redirect dopo il login
    localStorage.setItem('redirectAfterLogin', location.pathname);
    return <Navigate to="/login" />;
  }

  try {
    const payload = JSON.parse(atob(token.split('.')[1]));

    // Se è richiesta una protezione per ruolo specifico
    if (ruolo && payload.ruolo !== ruolo) {
      return <Navigate to="/home" />;
    }

    return children;
  } catch (err) {
    console.error("Errore parsing token:", err);
    return <Navigate to="/login" />;
  }
};

export default PrivateRoute;
