import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../auth/AuthContext'; // âœ… percorso corretto
import './Navbar.css';

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);

  return (
    <nav className="navbar">
      <div className="navbar-logo">Inquotus</div>
      <div className="navbar-links">
        {user && user.ruolo === 'committente' && (
          <>
            <Link to="/home">Le tue richieste</Link>
            <Link to="/nuova-richiesta">Inserisci richiesta</Link>
          </>
        )}

        {user && user.ruolo === 'impresa' && (
          <>
            <Link to="/impresa">Lavori disponibili</Link>
            <Link to="/sblocchi-effettuati">Contatti sbloccati</Link>
          </>
        )}

        {user && user.ruolo === 'professionista' && (
          <Link to="/professionista">Area professionista</Link>
        )}

        {user && user.ruolo === 'admin' && (
          <Link to="/admin/pagamenti">Pagamenti</Link>
        )}

        {user ? (
          <>
            <span className="navbar-user">
              ðŸ‘¤ {user.email}
              <span className={`badge badge-${user.ruolo}`}>{user.ruolo}</span>
            </span>
            <button className="logout-button" onClick={logout}>Logout</button>
          </>
        ) : (
          <>
            <Link to="/login">Login</Link>
            <Link to="/register">Registrati</Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
