useEffect(() => {
  const token = localStorage.getItem('token');
  if (token) {
    const payload = JSON.parse(atob(token.split('.')[1]));
    // Blocca i committenti da questa pagina
    if (payload.ruolo === 'committente') {
      navigate('/mie-richieste');
    } else {
      setUtente(payload);
    }
  }
}, [navigate]);

import React, { useEffect, useState } from 'react';
import { API_BASE } from '../config/config';
import { useNavigate } from 'react-router-dom';

const RichiesteLavoroInQuota = () => {
  const [richieste, setRichieste] = useState([]);
  const [provinciaFiltro, setProvinciaFiltro] = useState('');
  const [soloSbloccate, setSoloSbloccate] = useState(false);
  const [sblocchiEffettuati, setSblocchiEffettuati] = useState([]);
  const [loading, setLoading] = useState(false);
  const [utente, setUtente] = useState(null);  // Aggiungi stato per l'utente
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const payload = JSON.parse(atob(token.split('.')[1]));
      setUtente(payload);  // Salva il payload utente nel suo stato
    }

    const fetchDati = async () => {
      setLoading(true);
      try {
        // 1. Richieste pubbliche
        const resRichieste = await fetch(`${API_BASE}/api/richieste/pubbliche`);
        const pubbliche = await resRichieste.json();

        // 2. Sblocchi effettuati
        let sbloccateIds = [];
        if (token) {
          const resSblocchi = await fetch(`${API_BASE}/api/sblocchi-effettuati`, {
            headers: { Authorization: `Bearer ${token}` }
          });
          const sblocchi = await resSblocchi.json();
          sbloccateIds = sblocchi.map(r => r.id);
        }

        // 3. Tagga ogni richiesta
        const richiesteFinali = pubbliche.map(r => ({
          ...r,
          sbloccata: sbloccateIds.includes(r.id) ? 'si' : 'no'
        }));

        setRichieste(richiesteFinali);
        setSblocchiEffettuati(sbloccateIds);
      } catch (err) {
        console.error('Errore nel caricamento:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchDati();
  }, []);

  const handleSblocca = async (richiestaId) => {
    const token = localStorage.getItem('token');
    if (!token) return alert("Devi essere loggato");

    if (sblocchiEffettuati.includes(richiestaId)) {
      alert("Hai gi√† sbloccato questa richiesta.");
      return;
    }

    try {
      const res = await fetch(`${API_BASE}/api/checkout/sblocca`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({ richiesta_id: richiestaId })
      });

      const data = await res.json();

      if (data.url) {
        window.location.href = data.url;
      } else {
        alert('Errore nella creazione del pagamento.');
      }
    } catch (err) {
      console.error('Errore nel pagamento:', err);
      alert('Errore durante il processo di pagamento.');
    }
  };

  // Filtro per provincia e stato "solo sbloccate"
  const richiesteFiltrate = richieste.filter(r =>
    (provinciaFiltro === '' || r.provincia.toLowerCase().includes(provinciaFiltro.toLowerCase())) &&
    (!soloSbloccate || r.sbloccata === 'si')
  );

  return (
    <div style={{ padding: '1rem' }}>
      <h2>üì¢ Richieste di lavoro in quota</h2>
      {/* La frase "Visualizza tutte le richieste pubblicate..." √® stata rimossa */}

      {utente?.ruolo === 'impresa' || utente?.ruolo === 'professionista' ? (
        <div style={{ marginBottom: '1rem' }}>
          <input
            type="text"
            placeholder="Filtra per provincia (es. Milano)"
            value={provinciaFiltro}
            onChange={(e) => setProvinciaFiltro(e.target.value)}
            style={{ padding: '0.5rem', width: '100%', marginBottom: '0.5rem' }}
          />
          <label style={{ display: 'flex', alignItems: 'center' }}>
            <input
              type="checkbox"
              checked={soloSbloccate}
              onChange={(e) => setSoloSbloccate(e.target.checked)}
              style={{ marginRight: '0.5rem' }}
            />
            Mostra solo richieste sbloccate
          </label>
        </div>
      ) : null}

      {loading ? (
        <p>‚è≥ Caricamento...</p>
      ) : richiesteFiltrate.length === 0 ? (
        <p>Nessuna richiesta trovata.</p>
      ) : (
        <ul>
          {richiesteFiltrate.map((r) => (
            <li key={r.id} style={{ marginBottom: '1rem', border: '1px solid #ccc', padding: '1rem', borderRadius: '8px' }}>
              <p><strong>Categoria:</strong> {r.categoria}</p>
              <p><strong>Regione:</strong> {r.regione}</p>
              <p><strong>Provincia:</strong> {r.provincia}</p>
              <p><strong>Comune:</strong> {r.localita}</p>
              <p><strong>Descrizione:</strong> {r.descrizione.slice(0, 100)}...</p>
              <p><strong>Contatti sbloccati:</strong> {r.sbloccata === 'si' ? '‚úÖ S√¨' : 'üîí No'}</p>
              <button
                onClick={() =>
                  r.sbloccata === 'si'
                    ? navigate(`/richiesta/${r.id}`)
                    : handleSblocca(r.id)
                }
                style={{
                  marginTop: '0.5rem',
                  backgroundColor: r.sbloccata === 'si' ? 'gray' : '#007bff',
                  color: 'white',
                  border: 'none',
                  padding: '0.5rem 1rem',
                  cursor: 'pointer',
                  borderRadius: '5px'
                }}
              >
                {r.sbloccata === 'si' ? 'üëÅÔ∏è Visualizza richiesta' : 'üîì Sblocca contatti'}
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default RichiesteLavoroInQuota;
