import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = ({ setUtente }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const res = await fetch('https://inquotus-backend-auth.onrender.com/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });
      const data = await res.json();
      if (res.ok) {
        localStorage.setItem('token', data.token);

        const userRes = await fetch('https://inquotus-backend-auth.onrender.com/api/utente', {
          headers: { Authorization: `Bearer ${data.token}` }
        });
        const userData = await userRes.json();
        setUtente(userData);

        // üîÅ Redirect automatico in base al ruolo
        if (userData.ruolo === 'committente') {
          navigate('/committente');
        } else if (userData.ruolo === 'esecutore') {
          navigate('/impresa');
        } else if (userData.ruolo === 'professionista') {
          navigate('/professionista');
        } else {
          navigate('/');
        }

      } else {
        setMessage(data.error || 'Credenziali errate');
      }
    } catch (err) {
      setMessage('Errore di connessione al server');
    }
  };

  return (
    <div style={{ maxWidth: '400px', margin: '2rem auto' }}>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          required
        /><br /><br />

        <div style={{ position: 'relative' }}>
          <input
            type={showPassword ? "text" : "password"}
            placeholder="Password"
            value={password}
            onChange={e => setPassword(e.target.value)}
            required
            style={{ paddingRight: '2.5rem' }}
          />
          <span
            onClick={() => setShowPassword(!showPassword)}
            style={{
              position: 'absolute',
              right: '10px',
              top: '50%',
              transform: 'translateY(-50%)',
              cursor: 'pointer'
            }}
          >
            {showPassword ? 'üôà' : 'üëÅÔ∏è'}
          </span>
        </div><br />

        <button type="submit">Accedi</button>
      </form>
      <p>{message}</p>
    </div>
  );
};

export default Login;
