import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './Navbar';
import RichiestaForm from './RichiestaForm';
import Login from './Login';
import Register from './Register';
import RichiestePubbliche from './pages/RichiestePubbliche';
import DettaglioRichiesta from './pages/DettaglioRichiesta';

function App() {
  const [utente, setUtente] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      fetch('https://inquotus-backend-auth.onrender.com/api/utente', {
        headers: { Authorization: `Bearer ${token}` }
      })
        .then(res => res.json())
        .then(data => {
          if (data.id) setUtente(data);
        })
        .catch(() => localStorage.removeItem('token'));
    }
  }, []);

  return (
    <Router>
      <Navbar utente={utente} setUtente={setUtente} />
      <Routes>
        <Route path="/" element={<RichiestePubbliche />} />
        <Route path="/login" element={<Login setUtente={setUtente} />} />
        <Route path="/register" element={<Register />} />
        <Route path="/richiesta" element={<RichiestaForm userId={utente?.id} />} />
        <Route path="/richiesta/:id" element={<DettaglioRichiesta />} />
      </Routes>
    </Router>
  );
}

export default App;
