import React from 'react';
import { Link } from 'react-router-dom';

const Footer = () => {
  return (
    <footer style={{ padding: '2rem', backgroundColor: '#333', color: '#fff', textAlign: 'center' }}>
      {/* Logo e nome */}
      <div style={{ marginBottom: '1rem' }}>
        <img src="/path-to-logo.png" alt="Logo Inquotus" style={{ height: '40px' }} />
        <p style={{ fontSize: '1.2rem', margin: '0.5rem 0' }}>Inquotus - Piattaforma per lavori in quota</p>
      </div>

      {/* Informazioni di contatto */}
      <div style={{ marginBottom: '1rem' }}>
        <p>üìç Indirizzo: Via Esempio 123, Milano, Italia</p>
        <p>üìû Telefono: +39 123 456 789</p>
        <p>‚úâÔ∏è Email: <a href="mailto:info@inquotus.com" style={{ color: '#fff' }}>info@inquotus.com</a></p>
      </div>

      {/* Link legali */}
      <div style={{ marginBottom: '1rem' }}>
        <p>
          <Link to="/privacy" style={{ color: '#fff', margin: '0 1rem' }}>Privacy Policy</Link>|
          <Link to="/termini" style={{ color: '#fff', margin: '0 1rem' }}>Termini e Condizioni</Link>
        </p>
      </div>

      {/* Messaggio di sicurezza e copyright */}
      <div style={{ marginBottom: '1rem' }}>
        <p>üîê Sicurezza dei dati: Inquotus adotta misure avanzate per proteggere i tuoi dati personali e di pagamento. La piattaforma √® conforme al GDPR.</p>
        <p>&copy; {new Date().getFullYear()} Inquotus. Tutti i diritti riservati.</p>
      </div>

      {/* Social media links (opzionale) */}
      <div>
        <a href="https://facebook.com/inquotus" target="_blank" rel="noopener noreferrer" style={{ margin: '0 1rem' }}>
          <img src="/path-to-facebook-icon.png" alt="Facebook" style={{ height: '25px' }} />
        </a>
        <a href="https://twitter.com/inquotus" target="_blank" rel="noopener noreferrer" style={{ margin: '0 1rem' }}>
          <img src="/path-to-twitter-icon.png" alt="Twitter" style={{ height: '25px' }} />
        </a>
        <a href="https://linkedin.com/company/inquotus" target="_blank" rel="noopener noreferrer" style={{ margin: '0 1rem' }}>
          <img src="/path-to-linkedin-icon.png" alt="LinkedIn" style={{ height: '25px' }} />
        </a>
      </div>
    </footer>
  );
};

export default Footer;
