import React, { useState } from 'react';

const LocalitaForm = ({ onChange }) => {
  const [regione, setRegione] = useState('');
  const [comune, setComune] = useState('');

  const comuniPerRegione = {
    Lombardia: ['Milano', 'Bergamo', 'Brescia'],
    Lazio: ['Roma', 'Latina', 'Viterbo']
  };

  const handleRegioneChange = (e) => {
    console.log("ðŸ‘‰ Regione selezionata:", e.target.value);
    setRegione(e.target.value);
    onChange(e);
  };

  const handleComuneChange = (e) => {
    console.log("ðŸ‘‰ Comune selezionato:", e.target.value);
    setComune(e.target.value);
    onChange(e);
  };

  return (
    <div>
      <label>Regione:</label><br />
      <select name="regione" value={regione} onChange={handleRegioneChange} required>
        <option value="">-- Seleziona --</option>
        {Object.keys(comuniPerRegione).map((reg, idx) => (
          <option key={idx} value={reg}>{reg}</option>
        ))}
      </select><br /><br />

      <label>Comune:</label><br />
      <select name="comune" value={comune} onChange={handleComuneChange} required>
        <option value="">-- Seleziona --</option>
        {regione && comuniPerRegione[regione].map((c, idx) => (
          <option key={idx} value={c}>{c}</option>
        ))}
      </select><br /><br />

      <label>Via:</label><br />
      <input type="text" name="via" onChange={onChange} /><br /><br />

      <label>Civico:</label><br />
      <input type="text" name="civico" onChange={onChange} /><br />
    </div>
  );
};

export default LocalitaForm;
