import React, { useState } from 'react';
import { API_BASE } from '../config';

const Register = () => {
  const [formData, setFormData] = useState({
    nome: '',
    email: '',
    password: '',
    ruolo: 'committente'
  });
  const [message, setMessage] = useState('');

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();

    if (!/^(?=.*[a-z])(?=.*[A-Z])(?=.*\\d)(?=.*[^\\w\\s]).{6,}$/.test(formData.password)) {
      setMessage('⚠️ La password non rispetta i criteri di sicurezza.');
      return;
    }

    try {
      const res = await fetch(`${API_BASE}/api/register`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      const data = await res.json();
      if (res.ok) {
        setMessage('Registrazione completata!');
      } else {
        setMessage(data.error || 'Errore nella registrazione');
      }
    } catch (err) {
      setMessage('Errore di connessione al server');
    }
  };

  return (
    <div style={{ maxWidth: '400px', margin: '2rem auto' }}>
      <h2>Registrazione</h2>
      <form onSubmit={handleSubmit}>
        <input name="nome" placeholder="Nome" onChange={handleChange} required /><br /><br />
        <input type="email" name="email" placeholder="Email" onChange={handleChange} required /><br /><br />
        <input type="password" name="password" placeholder="Password" onChange={handleChange} required /><br /><br />
        <select name="ruolo" onChange={handleChange} value={formData.ruolo}>
          <option value="committente">Committente</option>
          <option value="esecutore">Impresa</option>
          <option value="professionista">Professionista</option>
        </select><br /><br />
        <button type="submit">Registrati</button>
      </form>
      <p>{message}</p>
    </div>
  );
};

export default Register;
