// src/pages/SbloccoSuccesso.jsx
import React, { useEffect, useState } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { API_BASE } from '../config/config';

const SbloccoSuccesso = () => {
  const [searchParams] = useSearchParams();
  const richiestaId = searchParams.get('richiestaId');
  const [inviata, setInviata] = useState(false);
  const [errore, setErrore] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (!richiestaId || !token) {
      setErrore('Parametri mancanti o non autenticato.');
      return;
    }

    fetch(`${API_BASE}/api/email-conferma-sblocco/${richiestaId}`, {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${token}`
      }
    })
      .then(res => res.json())
      .then(data => {
        if (data.message) {
          setInviata(true);
        } else {
          setErrore(data.error || 'Errore invio email.');
        }
      })
      .catch(() => setErrore('Errore rete o server.'));
  }, [richiestaId]);

  return (
    <div style={{ maxWidth: '600px', margin: '2rem auto' }}>
      <h2>âœ… Pagamento completato</h2>
      {errore && <p style={{ color: 'red' }}>{errore}</p>}
      {inviata && <p style={{ color: 'green' }}>ðŸ“§ Email di conferma inviata con successo!</p>}
      <p>Puoi ora visualizzare i contatti del committente nella tua area privata.</p>
      <button onClick={() => navigate('/sblocchi-effettuati')} style={{ marginTop: '1rem' }}>
        ðŸ“‹ Vai ai tuoi sblocchi
      </button>
    </div>
  );
};

export default SbloccoSuccesso;
