import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = () => {
  const navigate = useNavigate();
  const [utente, setUtente] = useState(null);
  const [showLogoutModal, setShowLogoutModal] = useState(false);
  const [temaScuro, setTemaScuro] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return;

    try {
      const payload = JSON.parse(atob(token.split('.')[1]));
      setUtente({
        nome: payload.nome || payload.email,
        ruolo: payload.ruolo
      });
    } catch (err) {
      console.error('Errore decoding token:', err);
      setUtente(null);
    }
  }, []);

  const logout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  const toggleTema = () => {
    setTemaScuro(!temaScuro);
    document.body.style.backgroundColor = !temaScuro ? '#222' : '#fff';
    document.body.style.color = !temaScuro ? '#fff' : '#000';
  };

  return (
    <nav style={{
      padding: '1rem',
      background: temaScuro ? '#333' : '#eee',
      color: temaScuro ? '#fff' : '#000',
      display: 'flex',
      justifyContent: 'space-between'
    }}>
      <div>
        <Link to="/">üè† Home</Link>{' | '}
        <Link to="/richieste-lavoro-in-quota">üîç Richieste di lavoro in quota</Link>{' | '}
        {utente && (
          <>
            <Link to="/home">Dashboard</Link>{' | '}
            <Link to="/sblocchi-effettuati">Sblocchi effettuati</Link>{' | '}
          </>
        )}
        <button onClick={toggleTema} style={{ marginLeft: '1rem', padding: '0.2rem 0.5rem' }}>
          {temaScuro ? '‚òÄÔ∏è Tema chiaro' : 'üåô Tema scuro'}
        </button>
      </div>

      {utente && (
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-end' }}>
          <span style={{ fontStyle: 'italic' }}>
            üîê Area riservata | üëã {utente.nome} ({utente.ruolo})
          </span>
          <button
            onClick={() => setShowLogoutModal(true)}
            style={{
              marginTop: '1rem',
              background: 'none',
              border: '1px solid #ccc',
              padding: '0.5rem 1rem',
              cursor: 'pointer',
              borderRadius: '5px',
              color: 'red'
            }}
          >
            üîì Logout
          </button>
        </div>
      )}

      {!utente && (
        <div>
          <Link to="/login">Login</Link>{' | '}
          <Link to="/register">Registrati</Link>
        </div>
      )}

      {/* Modal conferma logout */}
      {showLogoutModal && (
        <div style={{
          position: 'fixed', top: 0, left: 0, width: '100%', height: '100%',
          background: 'rgba(0,0,0,0.6)', display: 'flex', justifyContent: 'center', alignItems: 'center'
        }}>
          <div style={{
            background: '#fff', padding: '2rem', borderRadius: '8px', textAlign: 'center'
          }}>
            <p>Sei sicuro di voler uscire?</p>
            <button onClick={logout} style={{ marginRight: '1rem' }}>S√¨, esci</button>
            <button onClick={() => setShowLogoutModal(false)}>Annulla</button>
          </div>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
