import React, { useEffect, useState } from 'react';

const LocalitaForm = ({ onChange }) => {
  const [comuni, setComuni] = useState([]);
  const [regioni, setRegioni] = useState([]);
  const [comuniFiltrati, setComuniFiltrati] = useState([]);

  useEffect(() => {
    fetch('/comuni.json')
      .then(res => res.json())
      .then(data => {
        setComuni(data);
        const regioniUniche = [...new Set(data.map(c => c.regione))];
        setRegioni(regioniUniche.sort());
      })
      .catch(err => console.error('Errore comuni:', err));
  }, []);

  const handleRegione = e => {
    onChange(e);
    const regione = e.target.value;
    const filtrati = comuni.filter(c => c.regione === regione);
    setComuniFiltrati(filtrati);
  };

  return (
    <div>
      <label>Regione:</label><br />
      <select name="regione" onChange={handleRegione} required>
        <option value="">-- Seleziona una regione --</option>
        {regioni.map((r, i) => <option key={i} value={r}>{r}</option>)}
      </select><br /><br />

      <label>Comune:</label><br />
      <select name="comune" onChange={onChange} required>
        <option value="">-- Seleziona un comune --</option>
        {comuniFiltrati.map((c, i) => (
          <option key={i} value={c.nome}>{c.nome}</option>
        ))}
      </select><br /><br />

      <label>Via:</label><br />
      <input name="via" onChange={onChange} /><br /><br />

      <label>Civico:</label><br />
      <input name="civico" onChange={onChange} /><br />
    </div>
  );
};

export default LocalitaForm;
