import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = () => {
  const navigate = useNavigate();
  const [utente, setUtente] = useState(null);
  const [showMenu, setShowMenu] = useState(false);
  const [showLogoutModal, setShowLogoutModal] = useState(false);
  const [temaScuro, setTemaScuro] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return;

    try {
      const payload = JSON.parse(atob(token.split('.')[1]));
      setUtente({
        nome: payload.nome || payload.email,
        ruolo: payload.ruolo
      });
    } catch (err) {
      console.error('Errore decoding token:', err);
      setUtente(null);
    }
  }, []);

  const logout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  const isCommittente = utente?.ruolo === 'committente';

  const toggleTema = () => {
    setTemaScuro(!temaScuro);
    document.body.style.backgroundColor = !temaScuro ? '#222' : '#fff';
    document.body.style.color = !temaScuro ? '#fff' : '#000';
  };

  return (
    <nav style={{
      padding: '1rem',
      background: temaScuro ? '#333' : '#eee',
      color: temaScuro ? '#fff' : '#000',
      display: 'flex',
      justifyContent: 'space-between'
    }}>
      <div>
        <Link to="/">üè† Home</Link>{' | '}
        <Link to="/richieste-lavoro-in-quota">üîç Richieste di lavoro in quota</Link>{' | '}
        {utente && (
          <>
            <Link to="/home">Dashboard</Link>{' | '}
            {isCommittente && (
              <>
                <Link to="/mie-richieste">Le mie richieste</Link>{' | '}
                <Link to="/nuova-richiesta">Nuova richiesta</Link>{' | '}
              </>
            )}
            <Link to="/sblocchi-effettuati">Sblocchi effettuati</Link>{' | '}
          </>
        )}
        {/* Pulsante non necessario */}
      </div>

      {utente && (
        <div style={{ position: 'relative' }}>
          <span
            onClick={() => setShowMenu(!showMenu)}
            style={{ cursor: 'pointer', fontStyle: 'italic' }}
          >
            üîê Area riservata | üëã Benvenuto {utente.nome} ({utente.ruolo === 'impresa' ? 'Impresa' : 'Professionista'})
          </span>

          {showMenu && (
            <div style={{
              position: 'absolute',
              right: 0,
              backgroundColor: temaScuro ? '#444' : '#fff',
              border: '1px solid #ccc',
              padding: '0.5rem',
              marginTop: '0.5rem'
            }}>
              <button onClick={() => setShowLogoutModal(true)} style={{ color: 'red' }}>
                üîì Logout
              </button>
            </div>
          )}
        </div>
      )}

      {/* Modal conferma logout */}
      {showLogoutModal && (
        <div style={{
          position: 'fixed', top: 0, left: 0, width: '100%', height: '100%',
          background: 'rgba(0,0,0,0.6)', display: 'flex', justifyContent: 'center', alignItems: 'center'
        }}>
          <div style={{
            background: '#fff', padding: '2rem', borderRadius: '8px', textAlign: 'center'
          }}>
            <p>Sei sicuro di voler uscire?</p>
            <button onClick={logout} style={{ marginRight: '1rem' }}>S√¨, esci</button>
            <button onClick={() => setShowLogoutModal(false)}>Annulla</button>
          </div>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
