import React, { useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import RichiesteLavoroInQuota from '../richieste/RichiesteLavoroInQuota';
import { AuthContext } from '../context/AuthContext';
import useRealtimeNotifiche from '../hooks/useRealtimeNotifiche';

const Professionista = () => {
  const navigate = useNavigate();
  const { utente } = useContext(AuthContext);

  useEffect(() => {
    if (!utente) return;
    if (utente.ruolo !== 'professionista') navigate('/login');
  }, [utente, navigate]);

  useRealtimeNotifiche(utente?.email);

  return (
    <div style={{ padding: '1rem' }}>
      <p style={{ marginTop: '1rem' }}>
        Visualizza le <strong>richieste di lavoro in quota</strong> e sblocca i contatti dei committenti per candidarti.
      </p>

      <div style={{ marginTop: '2rem' }}>
        <RichiesteLavoroInQuota nascondiInfoUtente={true} />
      </div>
    </div>
  );
};

export default Professionista;
