import React, { useState } from 'react';
import RichiestaForm from './RichiestaForm';

function App() {
  const [token, setToken] = useState('');
  const [userId, setUserId] = useState(null);
  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const [message, setMessage] = useState('');

  const handleLoginChange = (e) => {
    setLoginData({ ...loginData, [e.target.name]: e.target.value });
  };

  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('https://inquotus-backend-auth.onrender.com/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(loginData)
      });
      const data = await res.json();
      if (res.ok) {
        setToken(data.token);
        const payload = JSON.parse(atob(data.token.split('.')[1]));
        setUserId(payload.id);
        setMessage('Login riuscito!');
      } else {
        setMessage(data.error || 'Errore di login');
      }
    } catch (error) {
      setMessage('Errore di connessione al server');
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '2rem' }}>
      <h1>Inquotus - Lavori in Quota</h1>
      {!token ? (
        <form onSubmit={handleLoginSubmit}>
          <input name="email" placeholder="Email" onChange={handleLoginChange} required /><br /><br />
          <input name="password" type="password" placeholder="Password" onChange={handleLoginChange} required /><br /><br />
          <button type="submit">Login</button>
          <p>{message}</p>
        </form>
      ) : (
        <>
          <p>{message}</p>
          <RichiestaForm userId={userId} />
        </>
      )}
    </div>
  );
}

export default App;
