import React, { useEffect, useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const [utente, setUtente] = useState(null);
  const [showLogoutModal, setShowLogoutModal] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return;

    try {
      const payload = JSON.parse(atob(token.split('.')[1]));
      setUtente({
        nome: payload.nome || payload.email,
        ruolo: payload.ruolo
      });
    } catch (err) {
      console.error('Errore decoding token:', err);
      setUtente(null);
    }
  }, []);

  const logout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  const isCommittente = utente?.ruolo === 'committente';
  const isImpresa = utente?.ruolo === 'impresa';
  const isProfessionista = utente?.ruolo === 'professionista';

  // Non mostrare navbar se nella home pubblica e non loggato
  if (location.pathname === '/' && !utente) return null;

  return (
    <>
      <nav style={{
        padding: '1rem',
        background: '#f2f2f2',
        color: '#000',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        position: 'relative'
      }}>
        <div>
          <Link to="/">üè† Home</Link>{' | '}
          <Link to="/richieste-lavoro-in-quota">üîç Richieste di lavoro in quota</Link>{' | '}

          {utente && (
            <>
              <Link to="/home">Dashboard</Link>{' | '}

              {isCommittente && (
                <>
                  <Link to="/mie-richieste">Le mie richieste</Link>{' | '}
                  <Link to="/nuova-richiesta">Nuova richiesta</Link>{' | '}
                </>
              )}

              {(isImpresa || isProfessionista) && (
                <Link to="/sblocchi-effettuati">Sblocchi effettuati</Link>
              )}
            </>
          )}
        </div>

        {utente && (
          <button onClick={() => setShowLogoutModal(true)} style={{ color: 'red' }}>
            üîì Logout
          </button>
        )}
      </nav>

      {utente && (
        <div style={{
          position: 'absolute',
          top: 10,
          right: 20,
          fontWeight: 'bold',
          fontSize: '1rem',
          backgroundColor: '#fff',
          padding: '0.3rem 0.8rem',
          borderRadius: '6px',
          boxShadow: '0 0 5px rgba(0,0,0,0.1)'
        }}>
          üëã Benvenuto, {utente.nome} ({utente.ruolo})
        </div>
      )}

      {showLogoutModal && (
        <div style={{
          position: 'fixed', top: 0, left: 0, width: '100%', height: '100%',
          background: 'rgba(0,0,0,0.6)', display: 'flex', justifyContent: 'center', alignItems: 'center',
          zIndex: 999
        }}>
          <div style={{
            background: '#fff', padding: '2rem', borderRadius: '8px', textAlign: 'center'
          }}>
            <p>Sei sicuro di voler uscire?</p>
            <button onClick={logout} style={{ marginRight: '1rem' }}>S√¨, esci</button>
            <button onClick={() => setShowLogoutModal(false)}>Annulla</button>
          </div>
        </div>
      )}
    </>
  );
};

export default Navbar;
