import React, { useState } from 'react';
import CategoriaForm from './CategoriaForm';
import LocalitaForm from './LocalitaForm';

const RichiestaForm = ({ utente }) => {
  const [formData, setFormData] = useState({
    categoria: '',
    regione: '',
    comune: '',
    via: '',
    civico: '',
    descrizione: '',
    media_url: ''
  });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { categoria, regione, comune, via, civico, descrizione, media_url } = formData;
    const localita = `${regione}, ${comune}${via ? ', ' + via : ''}${civico ? ' ' + civico : ''}`;

    try {
      const res = await fetch('http://localhost:5000/api/richiesta', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          user_id: utente.id,
          categoria,
          localita,
          descrizione,
          media_url
        })
      });

      const data = await res.json();
      setMessage(res.ok ? '✅ Richiesta inviata con successo!' : data.error || 'Errore invio richiesta');
    } catch (err) {
      setMessage('Errore di connessione al server');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: '600px', margin: '2rem auto' }}>
      <h2>Nuova richiesta</h2>

      <CategoriaForm onChange={handleChange} /><br />
      <LocalitaForm onChange={handleChange} /><br />

      <label>Descrizione:</label><br />
      <textarea
        name="descrizione"
        value={formData.descrizione}
        onChange={handleChange}
        placeholder="Descrizione dettagliata"
        required
        style={{ width: '100%', height: '100px' }}
      /><br /><br />

      <label>Link immagine:</label><br />
      <input
        type="text"
        name="media_url"
        value={formData.media_url}
        onChange={handleChange}
        placeholder="https://..."
      /><br /><br />

      <button type="submit">Invia richiesta</button>
      <p>{message}</p>
    </form>
  );
};

export default RichiestaForm;
