import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { API_BASE } from '../config';

const DettaglioRichiesta = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [dettaglio, setDettaglio] = useState(null);
  const [errore, setErrore] = useState('');
  const [caricamento, setCaricamento] = useState(true);
  const [prezzoCorrente, setPrezzoCorrente] = useState(null);
  const [richiestaNonDisponibile, setRichiestaNonDisponibile] = useState(false);
  const [messaggioSuccesso, setMessaggioSuccesso] = useState('');

  const fetchDettaglio = async () => {
    setCaricamento(true);
    try {
      const res = await fetch(`${API_BASE}/api/richiesta/${id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (res.status === 410) {
        setRichiestaNonDisponibile(true);
        return;
      }

      const data = await res.json();
      setDettaglio(data);
    } catch {
      setErrore('Errore nel caricamento');
    } finally {
      setCaricamento(false);
    }
  };

  const sbloccaContatti = async () => {
    try {
      const res = await fetch(`${API_BASE}/api/sblocca/${id}`, {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (res.status === 410) {
        setRichiestaNonDisponibile(true);
        return;
      }

      if (res.ok) {
        const json = await res.json();
        setPrezzoCorrente(json.prezzoPagato);
        setMessaggioSuccesso('‚úÖ Contatti sbloccati con successo');
        fetchDettaglio();
      } else {
        alert('Errore durante lo sblocco');
      }
    } catch {
      alert('Errore di rete');
    }
  };

  useEffect(() => {
    fetchDettaglio();
  }, [id]);

  if (caricamento) return <p>Caricamento...</p>;
  if (errore) return <p style={{ color: 'red' }}>{errore}</p>;
  if (richiestaNonDisponibile) return <p style={{ color: 'red' }}>‚ùå Richiesta non pi√π disponibile.</p>;
  if (!dettaglio) return <p>Richiesta non trovata.</p>;

  return (
    <div style={{ maxWidth: '600px', margin: '2rem auto' }}>
      <h2>{dettaglio.categoria}</h2>
      <p><strong>Localit√†:</strong> {dettaglio.localita}</p>
      <p><strong>Data inserimento:</strong> {new Date(dettaglio.data_inserimento).toLocaleDateString()}</p>
      <p><strong>Descrizione:</strong> {dettaglio.descrizione}</p>
      {dettaglio.media_url && (
        <img src={dettaglio.media_url} alt="Allegato" style={{ maxWidth: '100%', marginTop: '1rem' }} />
      )}

      <hr />

      {messaggioSuccesso && <p style={{ color: 'green' }}>{messaggioSuccesso}</p>}

      {dettaglio.contatti ? (
        <div style={{ marginTop: '1rem' }}>
          <h3>Contatti del committente</h3>
          <p><strong>Nome:</strong> {dettaglio.contatti.nome}</p>
          <p><strong>Email:</strong> {dettaglio.contatti.email}</p>
          <p><strong>Telefono:</strong> {dettaglio.contatti.telefono}</p>
          {prezzoCorrente && <p><em>Prezzo pagato: {prezzoCorrente} ‚Ç¨</em></p>}
        </div>
      ) : (
        <div style={{ marginTop: '1rem' }}>
          <button onClick={sbloccaContatti}>
            üîì Contatta committente
          </button>
          {dettaglio.prezzo && (
            <p style={{ marginTop: '0.5rem', fontStyle: 'italic' }}>
              Prezzo attuale: {dettaglio.prezzo} ‚Ç¨
            </p>
          )}
        </div>
      )}

      <button onClick={() => navigate(-1)} style={{ marginTop: '2rem' }}>
        üîô Torna indietro
      </button>
    </div>
  );
};

export default DettaglioRichiesta;
