import React, { useEffect, useState } from 'react';
import { API_BASE } from '../config/config'; // âœ… percorso corretto

const SblocchiEffettuati = () => {
  const [sbloccati, setSbloccati] = useState([]);
  const [errore, setErrore] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return setErrore('Utente non autenticato');

    fetch(`${API_BASE}/api/sbloccati`, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
      .then(res => {
        if (!res.ok) throw new Error('Errore caricamento');
        return res.json();
      })
      .then(setSbloccati)
      .catch(err => setErrore(err.message));
  }, []);

  return (
    <div style={{ maxWidth: '800px', margin: '2rem auto' }}>
      <h2>ðŸ“„ Sblocchi effettuati</h2>
      {errore && <p style={{ color: 'red' }}>{errore}</p>}
      {sbloccati.length === 0 && !errore && <p>Nessuna richiesta sbloccata trovata.</p>}

      <ul style={{ listStyle: 'none', padding: 0 }}>
        {sbloccati.map((r) => (
          <li key={r.richiesta_id} style={{ border: '1px solid #ccc', padding: '1rem', marginBottom: '1rem', borderRadius: '8px' }}>
            <strong>Categoria:</strong> {r.categoria}<br />
            <strong>LocalitÃ :</strong> {r.localita}<br />
            <strong>Data richiesta:</strong> {new Date(r.data_inserimento).toLocaleString()}<br />
            <strong>Data sblocco:</strong> {new Date(r.data_sblocco).toLocaleString()}<br />
            <p>{r.descrizione}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SblocchiEffettuati;
