import React, { useEffect, useState } from 'react';

const LocalitaForm = ({ onChange }) => {
  const [dati, setDati] = useState([]);
  const [regioni, setRegioni] = useState([]);
  const [comuni, setComuni] = useState([]);

  const [regioneSelezionata, setRegioneSelezionata] = useState('');

  useEffect(() => {
    fetch('/comuni.json')
      .then((res) => res.json())
      .then((data) => {
        setDati(data);
        const tutteLeRegioni = [...new Set(data.map(c => c.regione))];
        setRegioni(tutteLeRegioni.sort());
      });
  }, []);

  const handleRegioneChange = (e) => {
    const regione = e.target.value;
    setRegioneSelezionata(regione);
    const comuniFiltrati = dati.filter(c => c.regione === regione).map(c => c.nome).sort();
    setComuni(comuniFiltrati);
    onChange(e);
  };

  return (
    <div>
      <label>Regione:</label><br />
      <select name="regione" value={regioneSelezionata} onChange={handleRegioneChange} required>
        <option value="">-- Seleziona Regione --</option>
        {regioni.map((r, idx) => (
          <option key={idx} value={r}>{r}</option>
        ))}
      </select><br /><br />

      <label>Comune:</label><br />
      <select name="comune" onChange={onChange} required>
        <option value="">-- Seleziona Comune --</option>
        {comuni.map((c, idx) => (
          <option key={idx} value={c}>{c}</option>
        ))}
      </select><br /><br />

      <label>Via:</label><br />
      <input name="via" placeholder="Via" onChange={onChange} /><br /><br />

      <label>Numero Civico:</label><br />
      <input name="civico" placeholder="Numero civico" onChange={onChange} /><br />
    </div>
  );
};

export default LocalitaForm;
