import React, { useState } from 'react';
import { API_BASE } from '../config';

const RichiestaForm = () => {
  const [categoria, setCategoria] = useState('');
  const [localita, setLocalita] = useState('');
  const [descrizione, setDescrizione] = useState('');
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    const token = localStorage.getItem('token');
    let media_url = '';

    try {
      // 1. Upload immagine
      if (file) {
        const formData = new FormData();
        formData.append('immagine', file);

        const uploadRes = await fetch(`${API_BASE}/api/upload`, {
          method: 'POST',
          headers: {
            Authorization: `Bearer ${token}`,
          },
          body: formData,
        });

        const uploadData = await uploadRes.json();
        media_url = uploadData.url;
      }

      // 2. Invia richiesta con media_url
      const body = { categoria, localita, descrizione, media_url };

      const res = await fetch(`${API_BASE}/api/richiesta`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(body),
      });

      if (res.ok) {
        alert('Richiesta inviata!');
        setCategoria('');
        setLocalita('');
        setDescrizione('');
        setFile(null);
      } else {
        alert('Errore invio richiesta');
      }
    } catch (err) {
      console.error(err);
      alert('Errore imprevisto');
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ padding: '1rem' }}>
      <h2>Nuova Richiesta</h2>

      <input
        type="text"
        value={categoria}
        onChange={(e) => setCategoria(e.target.value)}
        placeholder="Categoria"
        required
      /><br />

      <input
        type="text"
        value={localita}
        onChange={(e) => setLocalita(e.target.value)}
        placeholder="LocalitÃ "
        required
      /><br />

      <textarea
        value={descrizione}
        onChange={(e) => setDescrizione(e.target.value)}
        placeholder="Descrizione"
        required
      /><br />

      <input type="file" onChange={handleFileChange} accept="image/*" /><br />

      <button type="submit" disabled={loading}>
        {loading ? 'Invio in corso...' : 'Invia richiesta'}
      </button>
    </form>
  );
};

export default RichiestaForm;
