import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import RichiesteMie from '../richieste/RichiesteMie';
import useRealtimeNotifiche from '../hooks/useRealtimeNotifiche';

const Committente = () => {
  const [utenteInfo, setUtenteInfo] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const payload = JSON.parse(atob(token.split('.')[1]));
      setUtenteInfo(payload);

      // Blocca ruoli non autorizzati
      if (!['committente'].includes(payload.ruolo)) {
        navigate('/login');
      }
    }
  }, [navigate]);

  useRealtimeNotifiche(utenteInfo?.email);

  return (
    <div style={{ padding: '1rem' }}>
      <p style={{ marginTop: '1rem' }}>
        Qui puoi <strong>gestire e monitorare le tue richieste</strong> di lavoro in quota. Puoi anche crearne di nuove.
      </p>

      {utenteInfo?.ruolo === 'committente' && (
        <div style={{ margin: '1.5rem 0' }}>
          <button
            onClick={() => navigate('/nuova-richiesta')}
            style={{
              padding: '0.5rem 1rem',
              backgroundColor: '#007bff',
              color: 'white',
              borderRadius: '5px',
              border: 'none',
              cursor: 'pointer'
            }}
          >
            âž• Nuova richiesta
          </button>
        </div>
      )}

      <div>
        <RichiesteMie />
      </div>
    </div>
  );
};

export default Committente;
