import React, { useEffect, useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import RichiesteMie from '../richieste/RichiesteMie';
import useRealtimeNotifiche from '../hooks/useRealtimeNotifiche';
import { AuthContext } from '../context/AuthContext';

const Committente = () => {
  const navigate = useNavigate();
  const { utente } = useContext(AuthContext);

  useEffect(() => {
    if (!utente) return;
    if (utente.ruolo !== 'committente') navigate('/login');
  }, [utente, navigate]);

  useRealtimeNotifiche(utente?.email);

  return (
    <div style={{ padding: '1rem' }}>
      <p style={{ marginTop: '1rem' }}>
        Qui puoi <strong>gestire e monitorare le tue richieste</strong> di lavoro in quota. Puoi anche crearne di nuove.
      </p>

      {utente?.ruolo === 'committente' && (
        <div style={{ margin: '1.5rem 0' }}>
          <button
            onClick={() => navigate('/nuova-richiesta')}
            style={{
              padding: '0.5rem 1rem',
              backgroundColor: '#007bff',
              color: 'white',
              borderRadius: '5px',
              border: 'none',
              cursor: 'pointer'
            }}
          >
            âž• Nuova richiesta
          </button>
        </div>
      )}

      <div>
        <RichiesteMie />
      </div>
    </div>
  );
};

export default Committente;
