import React, { useEffect, useState } from 'react';
import { API_BASE } from '../config/config';

const RichiesteMie = () => {
  const [richieste, setRichieste] = useState([]);
  const [errore, setErrore] = useState('');
  const [modificaAttiva, setModificaAttiva] = useState(null);
  const [formEdit, setFormEdit] = useState({
    categoria: '',
    localita: '',
    descrizione: '',
    media_url: '',
  });

  useEffect(() => {
    fetchRichieste();
  }, []);

  const fetchRichieste = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      setErrore('Utente non autenticato.');
      return;
    }

    try {
      const res = await fetch(`${API_BASE}/api/richieste/mie`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!res.ok) throw new Error('Errore nel recupero delle richieste');
      const data = await res.json();
      setRichieste(data);
    } catch (err) {
      setErrore(err.message);
    }
  };

  const handleDelete = async (id) => {
    const conferma = window.confirm('Sei sicuro di voler eliminare questa richiesta?');
    if (!conferma) return;

    const token = localStorage.getItem('token');
    try {
      const res = await fetch(`${API_BASE}/api/richiesta/${id}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!res.ok) throw new Error('Errore eliminazione');
      setRichieste(richieste.filter((r) => r.id !== id));
    } catch (err) {
      alert("Errore durante l'eliminazione");
    }
  };

  const startModifica = (r) => {
    setModificaAttiva(r.id);
    setFormEdit({
      categoria: r.categoria,
      localita: r.localita,
      descrizione: r.descrizione,
      media_url: r.media_url || '',
    });
  };

  const handleUpdate = async (id) => {
    const token = localStorage.getItem('token');
    try {
      const res = await fetch(`${API_BASE}/api/richiesta/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(formEdit),
      });

      if (!res.ok) throw new Error('Errore aggiornamento');

      fetchRichieste();
      setModificaAttiva(null);
    } catch (err) {
      alert('Errore durante la modifica');
    }
  };

  return (
    <div style={{ padding: '1rem' }}>
      <h2>Le tue richieste</h2>
      {errore && <p style={{ color: 'red' }}>{errore}</p>}
      {richieste.length === 0 && !errore && <p>Nessuna richiesta trovata.</p>}

      {richieste.map((r) => (
        <div
          key={r.id}
          style={{ marginBottom: '1.5rem', border: '1px solid #ccc', padding: '1rem', borderRadius: '8px' }}
        >
          {modificaAttiva === r.id ? (
            <div>
              <input
                value={formEdit.categoria}
                onChange={(e) => setFormEdit({ ...formEdit, categoria: e.target.value })}
                placeholder="Categoria"
              /><br />
              <input
                value={formEdit.localita}
                onChange={(e) => setFormEdit({ ...formEdit, localita: e.target.value })}
                placeholder="Località"
              /><br />
              <textarea
                value={formEdit.descrizione}
                onChange={(e) => setFormEdit({ ...formEdit, descrizione: e.target.value })}
                placeholder="Descrizione"
              /><br />
              <input
                value={formEdit.media_url}
                onChange={(e) => setFormEdit({ ...formEdit, media_url: e.target.value })}
                placeholder="URL immagine (facoltativo)"
              /><br />
              {formEdit.media_url && (
                <>
                  <img src={formEdit.media_url} alt="Anteprima" width="150" /><br />
                  <button onClick={() => setFormEdit({ ...formEdit, media_url: '' })}>
                    🧹 Rimuovi immagine
                  </button><br />
                </>
              )}
              <button onClick={() => handleUpdate(r.id)}>💾 Salva</button>
              <button onClick={() => setModificaAttiva(null)} style={{ marginLeft: '0.5rem' }}>❌ Annulla</button>
            </div>
          ) : (
            <>
              <strong>{r.categoria}</strong><br />
              <em>{r.localita}</em><br />
              <small>{new Date(r.data_inserimento).toLocaleString()}</small><br />
              <p>{r.descrizione}</p>
              {r.media_url && <img src={r.media_url} alt="Allegato" width="200" style={{ marginTop: '0.5rem' }} />}<br />
              <button onClick={() => startModifica(r)} style={{ marginRight: '0.5rem' }}>
                ✏️ Modifica
              </button>
              <button onClick={() => handleDelete(r.id)} style={{ backgroundColor: '#dc3545', color: 'white' }}>
                🗑️ Elimina
              </button>
            </>
          )}
        </div>
      ))}
    </div>
  );
};

export default RichiesteMie;
