import React, { useEffect, useState } from 'react';

const CategoriaForm = ({ onChange }) => {
  const [categorie, setCategorie] = useState({});

  useEffect(() => {
    fetch('/categorie_raggruppate.json')
      .then(res => res.json())
      .then(data => setCategorie(data))
      .catch(err => console.error('Errore categorie:', err));
  }, []);

  return (
    <div>
      <label>Categoria:</label><br />
      <select name="categoria" onChange={onChange} required>
        <option value="">-- Seleziona una categoria --</option>
        {Object.entries(categorie).map(([gruppo, lista]) => (
          <optgroup key={gruppo} label={gruppo}>
            {lista.map((cat, i) => (
              <option key={i} value={cat}>{cat}</option>
            ))}
          </optgroup>
        ))}
      </select>
    </div>
  );
};

export default CategoriaForm;
