import React, { useEffect, useState } from 'react';

const LocalitaForm = ({ onChange }) => {
  const [comuni, setComuni] = useState([]);
  const [regioni, setRegioni] = useState([]);
  const [comuniFiltrati, setComuniFiltrati] = useState([]);

  useEffect(() => {
    fetch('/comuni.json')
      .then(res => res.json())
      .then(data => {
        setComuni(data);
        const regioniUniche = [...new Set(data.map(c => c.regione))];
        setRegioni(regioniUniche.sort());
      })
      .catch(err => console.error('Errore caricamento comuni:', err));
  }, []);

  const handleRegioneChange = (e) => {
    const regione = e.target.value;
    onChange(e);
    const filtrati = comuni.filter(c => c.regione === regione);
    setComuniFiltrati(filtrati);
  };

  return (
    <div>
      <label htmlFor="regione">Regione:</label><br />
      <select name="regione" onChange={handleRegioneChange} required>
        <option value="">-- Seleziona una regione --</option>
        {regioni.map((r, index) => (
          <option key={index} value={r}>{r}</option>
        ))}
      </select><br /><br />

      <label htmlFor="comune">Comune:</label><br />
      <select name="comune" onChange={onChange} required>
        <option value="">-- Seleziona un comune --</option>
        {comuniFiltrati.map((c, index) => (
          <option key={index} value={c.nome}>{c.nome}</option>
        ))}
      </select><br /><br />

      <label htmlFor="via">Via:</label><br />
      <input type="text" name="via" placeholder="Via" onChange={onChange} /><br /><br />

      <label htmlFor="civico">Numero civico:</label><br />
      <input type="text" name="civico" placeholder="Civico" onChange={onChange} /><br />
    </div>
  );
};

export default LocalitaForm;
