import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { API_BASE } from '../config/config';

const RichiesteMie = () => {
  const [richieste, setRichieste] = useState([]);
  const [errore, setErrore] = useState('');
  const [messaggio, setMessaggio] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    caricaRichieste();
  }, []);

  const caricaRichieste = async () => {
    const token = localStorage.getItem('token');
    try {
      const res = await fetch(`${API_BASE}/api/richieste/mie`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const data = await res.json();
      if (Array.isArray(data)) {
        setRichieste(data);
      } else {
        setErrore(data.error || 'Errore nel recupero');
      }
    } catch (err) {
      console.error('Errore:', err);
      setErrore('Errore di connessione');
    }
  };

  const eliminaRichiesta = async (id) => {
    const conferma = window.confirm('Sei sicuro di voler eliminare questa richiesta?');
    if (!conferma) return;

    const token = localStorage.getItem('token');
    try {
      const res = await fetch(`${API_BASE}/api/richiesta/${id}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` }
      });
      const data = await res.json();

      if (res.ok) {
        setMessaggio('‚úÖ Richiesta eliminata con successo!');
        setRichieste(prev => prev.filter(r => r.id !== id));
      } else {
        setErrore(data.error || 'Errore durante l\'eliminazione');
      }
    } catch (err) {
      console.error('‚ùå Errore durante l\'eliminazione:', err);
      setErrore('Errore di rete durante l\'eliminazione');
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">üìã Le tue richieste</h2>

      {errore && <p className="text-red-600">{errore}</p>}
      {messaggio && <p className="text-green-600">{messaggio}</p>}

      {richieste.length === 0 && !errore ? (
        <p>Non hai ancora inserito richieste.</p>
      ) : (
        <ul className="space-y-4">
          {richieste.map((r) => (
            <li key={r.id} className="border rounded p-4 shadow">
              <p><strong>Categoria:</strong> {r.categoria}</p>
              <p><strong>Comune:</strong> {r.comune}</p>
              <p><strong>Data:</strong> {new Date(r.data_inserimento).toLocaleDateString()}</p>

              <div className="mt-2 space-x-2">
                <button
                  onClick={() => navigate(`/modifica-richiesta/${r.id}`)}
                  className="bg-yellow-500 text-white px-3 py-1 rounded hover:bg-yellow-600"
                >
                  ‚úèÔ∏è Modifica
                </button>
                <button
                  onClick={() => eliminaRichiesta(r.id)}
                  className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                >
                  üóëÔ∏è Elimina
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default RichiesteMie;
