import React, { useEffect, useState } from 'react';
import RichiestaForm from './RichiestaForm';

function App() {
  const [utente, setUtente] = useState(null);
  const [formLogin, setFormLogin] = useState({ email: '', password: '' });
  const [messaggio, setMessaggio] = useState('');

  // ðŸ” Al primo avvio: controlla token
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      fetch('https://inquotus-backend-auth.onrender.com/api/utente', {
        headers: { Authorization: `Bearer ${token}` }
      })
        .then(res => res.json())
        .then(data => {
          if (data.id) {
            setUtente(data);
          } else {
            setMessaggio('Token non valido');
          }
        })
        .catch(() => setMessaggio('Errore di connessione'));
    }
  }, []);

  // ðŸ” Login manuale
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('https://inquotus-backend-auth.onrender.com/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formLogin)
      });
      const data = await res.json();
      if (res.ok) {
        localStorage.setItem('token', data.token);
        setMessaggio('Login effettuato. Ricarica la pagina.');
      } else {
        setMessaggio(data.error || 'Errore login');
      }
    } catch {
      setMessaggio('Errore di connessione');
    }
  };

  // ðŸ§  Aggiorna campo login
  const handleLoginChange = (e) => {
    setFormLogin({ ...formLogin, [e.target.name]: e.target.value });
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '2rem' }}>
      <h1>Inquotus</h1>
      {utente ? (
        <>
          <p>Benvenuto, {utente.nome} ({utente.ruolo}) - {utente.email}</p>
          <RichiestaForm userId={utente.id} />
        </>
      ) : (
        <>
          <h3>Effettua il login</h3>
          <form onSubmit={handleLogin}>
            <input name="email" type="email" placeholder="Email" onChange={handleLoginChange} required /><br /><br />
            <input name="password" type="password" placeholder="Password" onChange={handleLoginChange} required /><br /><br />
            <button type="submit">Login</button>
          </form>
          <p>{messaggio}</p>
        </>
      )}
    </div>
  );
}

export default App;
