import React, { useState } from 'react';
import { API_BASE } from '../config/config';

const RichiestaForm = () => {
  const [categoria, setCategoria] = useState('');
  const [localita, setLocalita] = useState('');
  const [descrizione, setDescrizione] = useState('');
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [messaggio, setMessaggio] = useState('');

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    const token = localStorage.getItem('token');
    let media_url = '';

    try {
      // Upload immagine
      if (file) {
        const formData = new FormData();
        formData.append('immagine', file);

        const uploadRes = await fetch(`${API_BASE}/api/upload`, {
          method: 'POST',
          headers: { Authorization: `Bearer ${token}` },
          body: formData
        });

        const uploadData = await uploadRes.json();
        media_url = uploadData.url;
      }

      // Invio richiesta
      const body = { categoria, localita, descrizione, media_url };

      const res = await fetch(`${API_BASE}/api/richiesta`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify(body)
      });

      if (res.ok) {
        setMessaggio('‚úÖ Richiesta inviata con successo!');
        setCategoria('');
        setLocalita('');
        setDescrizione('');
        setFile(null);
      } else {
        const errData = await res.json();
        setMessaggio(`‚ùå Errore: ${errData.error || 'invio fallito'}`);
      }
    } catch (err) {
      console.error(err);
      setMessaggio('‚ùå Errore di rete o server');
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ padding: '1rem', maxWidth: '600px', margin: '0 auto' }}>
      <h2>üìù Nuova Richiesta</h2>

      <input
        type="text"
        value={categoria}
        onChange={(e) => setCategoria(e.target.value)}
        placeholder="Categoria"
        required
        style={{ display: 'block', marginBottom: '1rem', width: '100%' }}
      />

      <input
        type="text"
        value={localita}
        onChange={(e) => setLocalita(e.target.value)}
        placeholder="Localit√†"
        required
        style={{ display: 'block', marginBottom: '1rem', width: '100%' }}
      />

      <textarea
        value={descrizione}
        onChange={(e) => setDescrizione(e.target.value)}
        placeholder="Descrizione dettagliata"
        required
        rows={5}
        style={{ display: 'block', marginBottom: '1rem', width: '100%' }}
      />

      <input type="file" onChange={handleFileChange} accept="image/*" style={{ marginBottom: '1rem' }} />

      <button type="submit" disabled={loading}>
        {loading ? 'Invio in corso...' : 'Invia richiesta'}
      </button>

      {messaggio && (
        <p style={{ marginTop: '1rem', color: messaggio.startsWith('‚úÖ') ? 'green' : 'red' }}>
          {messaggio}
        </p>
      )}
    </form>
  );
};

export default RichiestaForm;
