import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { API_BASE } from '../config';

const DettaglioRichiesta = () => {
  const { id } = useParams();
  const [dettaglio, setDettaglio] = useState(null);
  const [errore, setErrore] = useState('');

  useEffect(() => {
    fetch(`${API_BASE}/api/richiesta/${id}`, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem('token')}`
      }
    })
      .then(res => res.json())
      .then(data => {
        if (data.error) setErrore(data.error);
        else setDettaglio(data);
      })
      .catch(() => setErrore('Errore nel caricamento'));
  }, [id]);

  if (errore) return <p>{errore}</p>;
  if (!dettaglio) return <p>Caricamento...</p>;

  return (
    <div style={{ maxWidth: '600px', margin: '2rem auto' }}>
      <h2>{dettaglio.categoria}</h2>
      <p><strong>Localit√†:</strong> {dettaglio.localita}</p>
      <p><strong>Data:</strong> {new Date(dettaglio.data).toLocaleDateString()}</p>
      <p><strong>Descrizione:</strong> {dettaglio.descrizione}</p>
      {dettaglio.media_url && <img src={dettaglio.media_url} alt="Allegato" style={{ maxWidth: '100%' }} />}
      {dettaglio.nome && (
        <div>
          <p><strong>Contatto:</strong> {dettaglio.nome}</p>
          <p><strong>Email:</strong> {dettaglio.email}</p>
        </div>
      )}
    </div>
  );
};

export default DettaglioRichiesta;
