// src/Login.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = ({ setUtente }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errore, setErrore] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('https://inquotus-backend-auth.onrender.com/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });
      const data = await res.json();
      if (!res.ok) return setErrore(data.error || 'Errore durante il login');

      localStorage.setItem('token', data.token);

      // Recupera dati utente
      const utenteRes = await fetch('https://inquotus-backend-auth.onrender.com/api/utente', {
        headers: { Authorization: `Bearer ${data.token}` }
      });
      const utenteData = await utenteRes.json();
      setUtente(utenteData);
      navigate('/richiesta');
    } catch (err) {
      setErrore('Errore di connessione al server');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ textAlign: 'center', marginTop: '2rem' }}>
      <h2>Login</h2>
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required /><br /><br />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required /><br /><br />
      <button type="submit">Accedi</button>
      <p style={{ color: 'red' }}>{errore}</p>
    </form>
  );
};

export default Login;
