import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { API_BASE } from '../config/config';

const RichiesteMie = () => {
  const [richieste, setRichieste] = useState([]);
  const [messaggio, setMessaggio] = useState('');
  const navigate = useNavigate();

  const caricaRichieste = () => {
    const token = localStorage.getItem('token');
    fetch(`${API_BASE}/api/richieste/mie`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) {
          setRichieste(data);
        } else {
          setMessaggio('‚ö†Ô∏è Errore caricamento richieste');
        }
      })
      .catch(() => setMessaggio('‚ùå Errore connessione al server'));
  };

  useEffect(() => {
    caricaRichieste();
  }, []);

  const eliminaRichiesta = async (id) => {
    if (!window.confirm('Confermi di voler eliminare questa richiesta?')) return;
    const token = localStorage.getItem('token');

    try {
      const res = await fetch(`${API_BASE}/api/richiesta/${id}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` }
      });

      if (res.ok) {
        setMessaggio('‚úÖ Richiesta eliminata con successo');
        caricaRichieste();
      } else {
        const err = await res.json();
        setMessaggio(`‚ùå Errore: ${err.error}`);
      }
    } catch {
      setMessaggio('‚ùå Errore rete');
    }
  };

  return (
    <div style={{ padding: '1rem' }}>
      <h2>üìã Le tue richieste</h2>

      {messaggio && (
        <p style={{ color: messaggio.startsWith('‚úÖ') ? 'green' : 'red' }}>{messaggio}</p>
      )}

      {richieste.length === 0 ? (
        <p>Non hai ancora inserito richieste.</p>
      ) : (
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {richieste.map(r => (
            <li key={r.id} style={{ border: '1px solid #ccc', borderRadius: '8px', padding: '1rem', marginBottom: '1rem' }}>
              <strong>{r.categoria}</strong><br />
              {r.localita} ({r.provincia}, {r.regione})<br />
              {new Date(r.data_inserimento).toLocaleDateString()}<br />
              <div style={{ marginTop: '0.5rem' }}>
                <Link to={`/modifica-richiesta/${r.id}`} style={{ marginRight: '1rem' }}>
                  ‚úèÔ∏è Modifica
                </Link>
                <button onClick={() => eliminaRichiesta(r.id)} style={{ color: 'red', background: 'none', border: 'none', cursor: 'pointer' }}>
                  üóëÔ∏è Elimina
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default RichiesteMie;
