import React, { useEffect, useState } from 'react';
import { API_BASE } from '../config/config';

const RichiesteMie = () => {
  const [richieste, setRichieste] = useState([]);
  const [errore, setErrore] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchRichieste = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await fetch(`${API_BASE}/api/richieste/mie`, {
          headers: { Authorization: `Bearer ${token}` }
        });

        const data = await res.json();
        if (!res.ok) {
          setErrore(data.error || 'Errore inatteso dal server');
        } else {
          setRichieste(data);
        }
      } catch (err) {
        console.error('Errore:', err);
        setErrore('‚ùå Errore di connessione al server');
      } finally {
        setLoading(false);
      }
    };

    fetchRichieste();
  }, []);

  return (
    <div style={{ padding: '1rem' }}>
      <h2>üìã Le tue richieste</h2>

      {loading && <p>Caricamento in corso...</p>}
      {errore && <p style={{ color: 'red' }}>{errore}</p>}

      {richieste.length === 0 && !errore && !loading && (
        <p>Non hai ancora inserito richieste.</p>
      )}

      <ul style={{ listStyle: 'none', padding: 0 }}>
        {richieste.map((r) => (
          <li key={r.id} style={{ border: '1px solid #ccc', padding: '1rem', marginBottom: '1rem' }}>
            <p><strong>Categoria:</strong> {r.categoria}</p>
            <p><strong>Localit√†:</strong> {r.localita} ({r.provincia}, {r.regione})</p>
            <p><strong>Descrizione:</strong> {r.descrizione}</p>
            <p><strong>Inserita il:</strong> {new Date(r.data_inserimento).toLocaleDateString()}</p>
            {r.urgenza && <p style={{ color: 'red' }}><strong>‚ö†Ô∏è Urgente</strong></p>}
            {r.media_url && (
              <div>
                <img src={r.media_url} alt="Allegato" style={{ maxWidth: '100%', maxHeight: '200px' }} />
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RichiesteMie;
