import React, { useEffect, useState } from 'react';
import RichiesteLavoroInQuota from '../richieste/RichiesteLavoroInQuota';

const Impresa = () => {
  const [utenteInfo, setUtenteInfo] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const payload = JSON.parse(atob(token.split('.')[1]));
      setUtenteInfo(payload);
    }
  }, []);

  return (
    <div style={{ padding: '1rem' }}>
      <h2>üè¢ Dashboard Impresa</h2>
      {utenteInfo && (
        <p><strong>üë§ {utenteInfo.email}</strong> | Ruolo: <strong>{utenteInfo.ruolo}</strong></p>
      )}

      <p style={{ marginTop: '1rem' }}>
        Qui puoi consultare tutte le <strong>richieste di lavoro in quota</strong> pubblicate dai committenti.
        Per contattare il committente, clicca su "üîì Sblocca contatti".
      </p>

      <div style={{ marginTop: '2rem' }}>
        <RichiesteLavoroInQuota />
      </div>
    </div>
  );
};

export default Impresa;
