import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const DettaglioRichiesta = () => {
  const { id } = useParams();
  const [dettagli, setDettagli] = useState(null);
  const [errore, setErrore] = useState('');

  useEffect(() => {
    const fetchDettagli = async () => {
      try {
        const res = await fetch(`https://inquotus-backend-auth.onrender.com/api/richiesta/${id}`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`
          }
        });
        const data = await res.json();
        if (res.ok) setDettagli(data);
        else setErrore(data.error || 'Errore nel caricamento');
      } catch (err) {
        setErrore('Errore connessione server');
      }
    };

    fetchDettagli();
  }, [id]);

  if (errore) return <p>{errore}</p>;
  if (!dettagli) return <p>Caricamento...</p>;

  return (
    <div>
      <h2>Dettagli richiesta</h2>
      <p><strong>Categoria:</strong> {dettagli.categoria}</p>
      <p><strong>Localit√†:</strong> {dettagli.localita}</p>
      <p><strong>Descrizione:</strong> {dettagli.descrizione}</p>
      {dettagli.media_url && (
        <div>
          <strong>Immagine:</strong><br />
          <img src={dettagli.media_url} alt="Media" style={{ maxWidth: '300px' }} />
        </div>
      )}
      <p><strong>Data:</strong> {new Date(dettagli.data).toLocaleDateString()}</p>

      {/* Mostra contatti solo se presenti */}
      {dettagli.nome && (
        <>
          <p><strong>Contatto:</strong> {dettagli.nome}</p>
          <p><strong>Email:</strong> {dettagli.email}</p>
        </>
      )}
    </div>
  );
};

export default DettaglioRichiesta;
