import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { API_BASE } from '../config/config';

const DettaglioRichiesta = () => {
  const { id } = useParams();
  const [richiesta, setRichiesta] = useState(null);
  const [sbloccata, setSbloccata] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');

    const fetchDati = async () => {
      try {
        // 1. Carica dettagli richiesta
        const resRichiesta = await fetch(`${API_BASE}/api/richiesta/${id}`, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        const richiestaData = await resRichiesta.json();
        setRichiesta(richiestaData);

        // 2. Carica richieste sbloccate
        const resSblocchi = await fetch(`${API_BASE}/api/sblocchi-effettuati`, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        const sblocchi = await resSblocchi.json();
        const idsSbloccati = sblocchi.map((r) => r.id);

        setSbloccata(idsSbloccati.includes(parseInt(id)));
      } catch (err) {
        console.error('Errore nel caricamento dettagli:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchDati();
  }, [id]);

  if (loading) return <p>⏳ Caricamento dettagli...</p>;
  if (!richiesta) return <p>❌ Richiesta non trovata.</p>;

  return (
    <div style={{ padding: '1rem' }}>
      <h2>📄 Dettagli richiesta</h2>
      <p><strong>Categoria:</strong> {richiesta.categoria}</p>
      <p><strong>Regione:</strong> {richiesta.regione}</p>
      <p><strong>Provincia:</strong> {richiesta.provincia}</p>
      <p><strong>Comune:</strong> {richiesta.localita}</p>
      <p><strong>Data:</strong> {new Date(richiesta.data_inserimento).toLocaleDateString()}</p>
      <p><strong>Descrizione:</strong><br />{richiesta.descrizione}</p>

      {sbloccata ? (
        <>
          <hr />
          <h3>📞 Contatti del committente</h3>
          <p><strong>Nome:</strong> {richiesta.nome_committente}</p>
          <p><strong>Email:</strong> {richiesta.email}</p>
          <p><strong>Telefono:</strong> {richiesta.telefono}</p>
        </>
      ) : (
        <p style={{ marginTop: '1rem', color: 'gray' }}>
          🔒 Contatti visibili solo dopo aver sbloccato la richiesta.
        </p>
      )}
    </div>
  );
};

export default DettaglioRichiesta;
