import React, { useState } from 'react';
import CategoriaForm from './CategoriaForm';
import LocalitaForm from './LocalitaForm';
import { API_BASE } from './config';

const RichiestaForm = ({ utente }) => {
  const [formData, setFormData] = useState({
    categoria: '',
    regione: '',
    comune: '',
    via: '',
    civico: '',
    descrizione: '',
    media_url: ''
  });

  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!utente || !utente.id) {
      setMessage('⚠️ Devi essere loggato per inviare la richiesta');
      return;
    }

    const { categoria, regione, comune, via, civico, descrizione, media_url } = formData;
    const localita = `${regione}, ${comune}${via ? ', ' + via : ''}${civico ? ' ' + civico : ''}`;

    try {
      const res = await fetch(`${API_BASE}/api/richiesta`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          user_id: utente.id,
          categoria,
          localita,
          descrizione,
          media_url
        })
      });

      const data = await res.json();
      setMessage(res.ok ? '✅ Richiesta inviata con successo!' : data.error || 'Errore nel salvataggio');
    } catch (err) {
      console.error('Errore:', err);
      setMessage('Errore di connessione al server');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: '600px', margin: '2rem auto' }}>
      <h2>Nuova richiesta di lavoro in quota</h2>
      <CategoriaForm onChange={handleChange} /><br /><br />
      <LocalitaForm onChange={handleChange} /><br /><br />
      <textarea name="descrizione" placeholder="Descrizione dettagliata" onChange={handleChange} required /><br /><br />
      <input name="media_url" placeholder="Link immagine (opzionale)" onChange={handleChange} /><br /><br />
      <button type="submit">Invia richiesta</button>
      <p>{message}</p>
    </form>
  );
};

export default RichiestaForm;
