import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

function Committente({ utente }) {
  const [richieste, setRichieste] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return;

    fetch('http://localhost:5000/api/mie-richieste', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => setRichieste(data))
      .catch(err => console.error('Errore caricamento richieste:', err));
  }, []);

  return (
    <div style={{ padding: '1rem' }}>
      <h2>Ciao {utente?.nome}!</h2>
      <p>Benvenuto nella tua area personale. Qui puoi:</p>
      <ul>
        <li><Link to="/richiesta">Inserire una nuova richiesta di lavoro in quota</Link></li>
      </ul>

      <h3>Le tue richieste</h3>
      {richieste.length === 0 ? (
        <p>Non hai ancora inserito richieste.</p>
      ) : (
        <ul>
          {richieste.map(r => (
            <li key={r.id} style={{ marginBottom: '1rem' }}>
              <strong>Categoria:</strong> {r.categoria} <br />
              <strong>Comune:</strong> {r.localita} <br />
              <strong>Data:</strong> {new Date(r.data).toLocaleDateString()}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Committente;
