import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const RichiestePubbliche = () => {
  const [richieste, setRichieste] = useState([]);

  useEffect(() => {
    fetch('https://inquotus-backend-auth.onrender.com/api/richieste')
      .then((res) => res.json())
      .then((data) => setRichieste(data))
      .catch((err) => console.error('Errore caricamento richieste:', err));
  }, []);

  return (
    <div>
      <h2>Richieste disponibili</h2>
      {richieste.length === 0 && <p>Nessuna richiesta trovata.</p>}
      <ul>
        {richieste.map((r) => (
          <li key={r.id} style={{ border: '1px solid #ccc', marginBottom: '1rem', padding: '1rem' }}>
            <strong>Categoria:</strong> {r.categoria}<br />
            <strong>Comune:</strong> {r.localita}<br />
            <strong>Data:</strong> {new Date(r.data).toLocaleDateString()}<br /><br />
            <Link to={`/richiesta/${r.id}`}>Vedi dettagli</Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RichiestePubbliche;
